import { Link,Switch, Route } from "react-router-dom";
import About from "./About";
import Contact from "./Contact";
import Dash from "./Dash";
import Footer from "./Footer";
import Header from "./Header";
import NotFound from "./NotFound";

import { useEffect, useState } from "react";
import { useHistory } from 'react-router-dom';
import Signup from "./Signup";
import Profile from "./Profile";


function  Launch()
{

      const [username, setUserName] = useState("User");
            const history = useHistory(); 

           const {SignIn,SignOut} =Auth();



      //    useEffect(()=>{
      //       if(sessionStorage.getItem("username")!=null)
      //       {
      //             setUserName(sessionStorage.getItem("username"));
      //       }
      //       else
      //       {
      //             setUserName("User");
      //       }
      //    },[]);




         const ShowButton =()=>{

            if(username == "User")
            {
                  return  <button className='btn btn-warning' onClick={SignIn}>Log In</button>
            }
            else
            {
                  return  <button className='btn btn-warning' onClick={()=>{SignOut(setUserName)}}>Log out</button>
            }
         }  
    return (<> 
                <Header></Header>
                 <hr></hr>

                 <div style={{paddingLeft:50}}>
                  Welcome {" "} {username}

                  {ShowButton()}


                 </div>
                 {"   |   "}
                 <Link to="/db">Dash</Link> 
                 {"   |   "}
                 <Link to="/about">About Us</Link>
                 {"   |   "}    
                 <Link to="/contact">Contact Us</Link>
                 {"   |   "}   
                 <Link to="/signup">Signup</Link>
                 {"   |   "}   
                 <Link to="/profile">Profile</Link>
                 <hr></hr>
                   <Switch>
                   <ProtectedRoute path="/db"
                        component={Dash} setUserName={setUserName}/>
                         <Route path="/about" 
                                exact
                               component={About}/>
                         <Route path="/contact" 
                                exact
                               component={Contact}/>
                        <Route path="/signup" 
                               exact
                               component={Signup}/>
                         <Route path="/profile" 
                               exact
                               component={Profile}/>
                        <ProtectedRoute path="/"
                        component={Dash} setUserName={setUserName}/>

                        <Route path="/profile" 
                               exact
                               component={Profile}/>
                         <Route path="*" 
                               component={NotFound}/>
                   </Switch>
                 <hr></hr>
                <Footer></Footer>
            </>);
}

export default Launch;
