import { useEffect, useState } from "react";
import '../node_modules/bootstrap/dist/css/bootstrap.css';
import './common.css'
import { useHistory } from 'react-router-dom';

function Signup()
{
    debugger;

    const [message, setmessage]=useState("");
    const [signup,setsignup] =  
            useState({username: "", password : ""});
    const history =useHistory();

    useEffect(()=>{
        if(message!=="")
        {
            setTimeout(()=>{
                setmessage("");
            },3000);
        }
    },[message])

    const OnTextChange=(args)=>{
        var copyOfsignup= 
                {...signup};
        copyOfsignup[args.target.name] = 
                args.target.value;
        setsignup(copyOfsignup);
    }


    const signUp=()=>
    {
        // this.state.empToBeAdded .. hold record's data
        // to be send to Node Server!
        var helper = new XMLHttpRequest();
        helper.onreadystatechange = ()=>{
            if(helper.readyState == 4 &&
                helper.status == 200)
                {
                    var responseReceived = 
                        JSON.parse(helper.responseText);
                    if(responseReceived.affectedRows!=undefined && responseReceived.affectedRows>0)
                    {
                        setmessage("Signup Successfull!")

                        setsignup({username: "", 
                                         password : ""});
                    }
                    else
                    {
                        setmessage("Something went wrong!")
                    }
                }
        };
        helper.open("POST","http://127.0.0.1:9999/signup");
        helper.setRequestHeader("Content-Type", "application/json")
        helper.send(JSON.stringify(signup));   


    }



    return (<div className='myTable'>
    <div className="form-group">
        <input type="test" className="form-control" 
        name="username" 
        placeholder="Enter username"
        value={signup.username}
        onChange={OnTextChange}/>
    </div>
    <div className="form-group">
        <input type="text" className="form-control" 
        name="password" 
        placeholder="Enter password"
        value={signup.password}
        onChange={OnTextChange}/>
    </div>

    <button className="btn btn-primary"
             onClick={signUp}>
        Add Record
    </button>


</div>);

