import { Link,Switch, Route } from "react-router-dom";
import About from "./About";
import Contact from "./Contact";
import Dash from "./Dash";
import Footer from "./Footer";
import Header from "./Header";
import NotFound from "./NotFound";
import ProtectedRoute from "./ProectedRoute";
import { useEffect, useState } from "react";
import { useHistory } from 'react-router-dom';

function  Launch()
{

      const [username, setUserName] = useState("User")
            const history = useHistory(); 
            const {SignIn, SignOut}=Auth();

      //   const SignOut = ()=>{
      //             sessionStorage.removeItem("isloggedin");
      //             sessionStorage.removeItem("username");
      //             setUserName("User");
      //             history.push("/db")
      //             }
      //    const SignIn = ()=>{
      //       history.push("/db")
      //    } this signout and sign in write in navigateToFromLogin

         useEffect(()=>{
            if(sessionStorage.getItem("username")!=null)
            {
                  setUserName(sessionStorage.getItem("username"));
            }
            else
            {
                  setUserName("user");
            }
         },[]);




         const ShowButton =()=>{
            if(username == "User")
            {
                  return  <button className='btn btn-warning' onClick={SignIn}>Log In</button>
            }
            else
            {
                  return  <button className='btn btn-warning' onClick={SignOut}>Log out</button>
            }
         }  
    return <>
                <Header></Header>
                 <hr></hr>
                 <div style={{paddingLeft:50}}>
                  Welcome {" "} 
                 </div>
                 {"   |   "}
                 <Link to="/db">Dash</Link> 
                 {"   |   "}
                 <Link to="/about">About Us</Link>
                 {"   |   "}    
                 <Link to="/contact">Contact Us</Link>
                 <hr></hr>
                   <Switch>
                   <ProtectedRoute path="/db"
                        component={Dash} setUserName={setUserName}/>
                         <Route path="/about" 
                                exact
                               component={About}/>
                         <Route path="/contact" 
                                exact
                               component={Contact}/>
                        <ProtectedRoute path="/"
                        component={Dash} setUserName={setUserName}/>
                         <Route path="*" 
                               component={NotFound}/>
                   </Switch>
                 <hr></hr>
                <Footer></Footer>
            </>
}

export default Launch;
