import { useEffect, useState } from "react";
import { Link, Switch, Route } from "react-router-dom";
import "../node_modules/bootstrap/dist/css/bootstrap.css";
import "./common.css";
import { useHistory } from "react-router-dom";
import ProtectedRoute from "./ProtectedRoute";
import { toast } from 'react-toastify'

function Signup() {

  const [firstName, setFirstName] = useState('')
  const [lastName, setLastName] = useState('')
  const [email, setEmail] = useState('')
  const [mobile, setMobile] = useState('')
  const [password, setPassword] = useState('')

  const [confirmPassword, setConfirmPassword] = useState('')
  const [gender, setgender] = useState('')
  const [role, setrole] = useState('')
  // const [message, setmessage] = useState("");
  // const [signup, setsignup] = useState({
  //   firstName: "",
  //   lastName: "",
  //   email: "",
  //   mobile: "",
  //   password: "",
  //   gender: "",
  //   branchId: 0,
  //   role: "",
  // });
  // const history = useHistory();
  // const [username, setUserName] = useState("User");
  // const newUser = (c) =>{
  //   setsignup({...signUp,[c.target.name]:c.target.value})
  // }

  useEffect(() => {
    if (message !== "") {
      setTimeout(() => {
        setmessage("");
      }, 3000);
    }
  }, [message]);

  const newUser = (args) => {
    var copyOfsignup = { ...signup };
    copyOfsignup[args.target.name] = args.target.value;
    setsignup(copyOfsignup);
  };

  const signUp = () => {
   debugger;
    var helper = new XMLHttpRequest();
    helper.onreadystatechange = () => {
      if (helper.readyState == 4 && helper.status == 200) {
        var responseReceived = JSON.parse(helper.responseText);
        if (
          responseReceived.affectedRows != undefined &&
          responseReceived.affectedRows > 0
        ) {
          setmessage("Signup Successfull!");

          setsignup({
            firstName: "",
            lastName: "",
            email: "",
            mobile: "",
            password: "",
            gender: "",
            branchId: 0,
            role: "",
          });

          <ProtectedRoute path="/" />;
        } else {
          setmessage("Something went wrong!");
          <ProtectedRoute path="/" />;
        }
      }
    };
    helper.open("POST", "http://127.0.0.1:7080/emps/add");
    helper.setRequestHeader("Content-Type", "application/json");
    helper.send(JSON.stringify(signup));
  };

  return (
    <center>
      <div className="length">
        <div className="myTable">
          <div className="form-group">
            <table>
              <tr>
                <td className="fc">Enter firstname</td>
                <td>
                  <input
                    type="text"
                    required
                    className="form-control"
                    name="firstName"
                    placeholder="Enter Firstname"
                    value={signup.firstname}
                    onChange={newUser}
                  />
                </td>
                <td className="td"></td>

                <td className="fc">Enter lastname </td>
                <td>
                  <input
                    type="text"
                    required
                    className="form-control"
                    name="lastName"
                    placeholder="Enter Lastname"
                    value={signup.lastname}
                    onChange={newUser}
                  />
                </td>
              </tr>

              <tr className="tr"></tr>

              <tr>
                <td className="fc">Enter Email</td>
                <td>
                  <input
                    type="text"
                    required
                    className="form-control"
                    name="email"
                    placeholder="Enter Email"
                    value={signup.email}
                    onChange={newUser}
                  />
                </td>
                <td className="td"></td>
                <td className="fc">Enter Mobile</td>

                <td>
                  <input
                    type="number"
                    required
                    className="form-control"
                    name="mobile"
                    placeholder="Enter Mobile number"
                    value={signup.mobile}
                    onChange={newUser}
                  />
                </td>
              </tr>

              <tr className="tr"></tr>

              <tr>
                <td className="fc">Enter Password</td>
                <td>
                  <input
                    type="password"
                    required
                    className="form-control"
                    name="password"
                    placeholder="Enter password"
                    value={signup.password}
                    onChange={newUser}
                  />
                </td>

                <td className="td"></td>
                <td className="fc">Enter Gender</td>
                <td>
                  <select
                    name="gender"
                    value={signup.gender}
                    onChange={newUser}
                  >
                    <option value="Select">Select Gender</option>
                    <option value="Male">Male</option>
                    <option value="Female">Female</option>
                  </select>
                </td>
              </tr>
              <tr className="tr"></tr>

              <tr>
                <td className="fc">Enter BranchId</td>
                <td>
                  <input
                    type="number"
                    required
                    className="form-control"
                    name="branchId"
                    placeholder="Enter branchId"
                    value={signup.branchid}
                    onChange={newUser}
                  />
                </td>

                <td className="td"></td>
                <td className="fc">Enter role</td>
                <td>
                  <select name="role" value={signup.role} onChange={newUser}>
                    <option value="Select">Select Role</option>
                    <option value="Deliveryboy">Deliveryboy</option>
                    <option value="Admin">Admin</option>
                  </select>
                </td>
              </tr>
            </table>
          </div>
          <tr className="tr"></tr>

          <div className="mytable alert alert-warning">
            <h1> {message}</h1>
          </div>
          <hr></hr>

          <button className="btn btn-primary" onClick={signUp}>
            Add Record
          </button>
        </div>
      </div>
    </center>
  );
}

export default Signup;
