import axios from 'axios'
import { createUrl, log } from '../utils/utils'
export async function getOrdersList(customerId) {
    const url = createUrl(`/customer/${customerId}`);

    try {
      // get the current user's token from session storage
      // const { token } = sessionStorage

      // // create a header to send the token
      // const header = {
      //   headers: {
      //     token,
      //   },
      // }

      // make the api call using the token in the header
      const response = await axios.get(url);
      log(response.data);
      return response.data;
    } catch (ex) {
      log(ex);
      return null;
    }
  }

  export async function signUp(
    firstName,
    lastName,
    email,
    password,
    mobile,
    gender,

  ) {
    const url = createUrl('/customer/signup')

    const body = {
      firstName,
      lastName,
      email,
      password,
      mobile,
      gender,

    }

    // wait till axios is making the api call and getting response from server
    try {
      const response = await axios.post(url, body)
      log(response.data)
      // console.log(response.data)
      return response.data

    } catch (ex) {
      log(ex)
      return null
    }
  }
