import { useEffect,useState } from "react";

function Sample()
{
  var [message,setMessage]=useState("Hi");
  var [company,setCompany]=useState("Sunbeam");



useEffect(()=>{
    console.log("this is like componen did mont");
},[]);

useEffect(()=>{
    console.log("this is like component did update");
    console.log("thiscode executed coz message changed");
},[message]);

useEffect(()=>{
    console.log("this is like component did update");
    console.log("thiscode executed coz company changed");
},[company]);

useEffect(()=>{
    console.log("this is like component did update");
    console.log("thiscode executed coz message or company changed");
},[message, company]);

var DoChange=()=>{
    console.log("Inside button click");
    console.log("set value for message and company");
    setMessage("hello");
    setCompany("Infosys");
}

var DoChange1=()=>{
    console.log("change name");
    setMessage("1234");
}
var DoChange2=()=>{
    console.log("change company");
    setCompany("Synechron");
}

console.log("about to render UI");
return <div>
    <h1>{message}</h1>
    <h1>{company}</h1>
    <button onClick={DoChange1}>Change Message</button>
    <br></br>
    <button onClick={DoChange2}>Change Company</button>
    <br></br>
    <button onClick={DoChange}>Change Message and Company</button>
    <br></br>


</div>
}
export default Sample;
