import {useState} from "react";
import Day from "./Day";

function Demo()
{
    const [days, setDays]=useState(["Mon", "Tue","Wed","Thur","Fri"]);
    const Remove =(dataToBeRemoved)=>{

        var fileterdDays=new Array();
        for(let i=0;i<days.length;i++)
        {
            if(days[i]!=dataToBeRemoved)
            {
                fileterdDays.push(days[i]);
            }
        }
        setDays(fileterdDays);
    }

    return <>
          <center>
            {
            days.map(day=>{
                return <Day key={day}
                            day={day}
                            Delete={Remove}/>
            })
        }
          </center>
          </>
}

export default Demo;














