import { useEffect, useState } from "react";
import { Link,Switch, Route } from "react-router-dom";
import '../node_modules/bootstrap/dist/css/bootstrap.css';
import './common.css'
import { useHistory } from 'react-router-dom';

import image from './images/counter.jpg';



function Signup()
{
    debugger;

    const [message, setmessage]=useState("");
    const [signup,setsignup] =  
            useState({username: "", password : ""});
    const history =useHistory();
    const [username, setUserName] = useState("User");

    useEffect(()=>{
        if(message!=="")
        {
            setTimeout(()=>{
                setmessage("");
            },3000);
        }
    },[message])

    const OnTextChange=(args)=>{
        var copyOfsignup= 
                {...signup};
        copyOfsignup[args.target.name] = 
                args.target.value;
        setsignup(copyOfsignup);
    }


    const signUp=()=>
    {
        debugger;
        // this.state.empToBeAdded .. hold record's data
        // to be send to Node Server!
        var helper = new XMLHttpRequest();
        helper.onreadystatechange = ()=>{
            if(helper.readyState == 4 &&
                helper.status == 200)
                {
                    var responseReceived = 
                        JSON.parse(helper.responseText);
                    if(responseReceived.affectedRows!=undefined && responseReceived.affectedRows>0)
                    {
                        setmessage("Signup Successfull!")

                        setsignup({username: "", 
                                         password : ""});

                                         <ProtectedRoute path="/"
                                         component={Dash} />             
                    }
                    else
                    {
                        setmessage("Something went wrong!");
                        <ProtectedRoute path="/"
                        component={Dash}/>  
                    }
                }
        };
        helper.open("POST","http://127.0.0.1:9999/signup");
        helper.setRequestHeader("Content-Type", "application/json")
        helper.send(JSON.stringify(signup));   

        <Route path="/"
              component={Dash} /> 
    }



    return (
        <center>
        <div style={{backgroundImage:`url(${image})`}} >
    <div className="length" >

    <div className='myTable'>
    <div className="form-group">
        <table>

            <tr>

                <td className="fc">Enter firstname</td>
                <td>
                <input type="text" required className="form-control" 
        name="userfirstname" 
        placeholder="Enter Firstname"
       />
                </td>
                <td  className="td"></td>

               <td className="fc">Enter lastname </td>
                <td>
                <input type="text" required className="form-control" 
        name="userlastname" 
        placeholder="Enter Lastname"
       />
                </td>



            </tr>

             <tr className="tr"></tr>

            <tr>

                <td className="fc">Enter Email</td>
                <td>  <input type="text" required className="form-control" 
        name="email" 
        placeholder="Enter Email"
        /></td>
          <td className="td"></td>
        <td className="fc">
        Enter Mobile 
        </td>

        <td>
                <input type="text" required className="form-control" 
        name="mobile" 
        placeholder="Enter Mobile number"
      />
                </td>
            </tr>

            <tr className="tr"></tr>


            <tr>
                <td  className="fc">
                    Enter Password
                </td>
                <td>
                <input type="text" required className="form-control" 
        name="password" 
        placeholder="Enter password"

        /> 

                </td>
                <td className="td"></td>
                <td className="fc">Enter Gender</td>
                <td>
                        <select name="role" >
                                    <option value="Select">Select Role</option>
                                    <option value="">Male</option>
                                    <option value="">Female</option>
                                </select>
                        </td>
            </tr>
        </table>

    </div>
    <tr className="tr"></tr>

    <div className='mytable alert alert-warning'>
                       <h1> {message}</h1>
                    </div>
                    <hr></hr>

    <button className="btn btn-primary"
             onClick={signUp}>
        Add Record
    </button>

    </div> 
    </div>
</div>
</center>);





}




export default Signup;
